UPDATE prime_celeb left join data_lake.MK_CELEB m on m.ID = prime_celeb.primary_src_id SET prime_celeb.celeb_name = m.NAME_E where prime_celeb.date >= subdate(current_date, interval 7 day); 
UPDATE sec_celeb left join data_lake.MK_CELEB m on m.ID = sec_celeb.sec_src_id SET sec_celeb.celeb_name = m.NAME_E where sec_celeb.date >= subdate(current_date, interval 7 day); 
insert into prime_sec_traffic select p.date, p.primary_src_id, p.celeb_name, p.users, p.primary_sessions, s.sec_sessions from (select date, primary_src_id, celeb_name, sum(sessions) primary_sessions, sum(users) users from prime_celeb where date >= subdate(current_date, interval 1 day) group by primary_src_id, date) p left join (select date, sec_src_id, sum(sessions) sec_sessions from sec_celeb where date >= subdate(current_date, interval 1 day) group by sec_src_id, date) s on p.primary_src_id = s.sec_src_id and p.date=s.date group by p.date, p.primary_src_id;
