CREATE OR REPLACE VIEW analytics.inventory_health_1 AS
SELECT ih.sku, ih.barcode, ih.sku_name, ih.brand, ih.department, ih.category1, ih.category2, ih.vendor_code, ih.supplier, ih.first_grn_date, ih.first_order_date, ih.last_order_date, ih.soh, ih.open_po_qty, ih.crs_available_qty, ih.first_sale_in_60_days, ih.days_sold_60days, ih.sold_qty_07days, ih.sold_qty_14days, ih.sold_qty_30_days, ih.sold_qty_60_days, ih.sold_qty_90_days, ih.sold_qty_180_days, ih.lifetime_sold_qty, ih.gift_qty, ih.stock_cover_60days_sale_basis, ih.stock_cover_60days_including_open_po, ih.stock_cover_flag, ih.stock_cover_flag_including_open_po, ih.inventory_category, ih.firt_grn_to_first_sale_days, ih.sku_live_status, ih.events_count_30days, ih.events_count_60days, ih.events_count_90days, COALESCE(pc.parent_sku, ih.sku) AS parent_sku FROM (aoi.inventory_health ih LEFT JOIN analytics.parent_child_sku_mapping pc ON (((ih.sku)::text = (pc.child_sku)::text)))
WITH NO SCHEMA BINDING;